settings.custom_css:
/* Disable sumbit button by hiding real button and replacing with dummy button*/

.elementor-field-group elementor-column elementor-field-type-submit elementor-col-30 e-form__buttons elementor-md-60{
    display: none;
}

/*
Text box overlay for us page
*/
.cstm-text .elementor-widget-container {
    background: white;
    border-radius: 12px;
}
/*
Main design gradient background
*/

.design-gradient {
	background: linear-gradient(90deg, rgba(255, 207, 227, 0.54) 0%, rgba(255, 197, 228, 0.24) 47.4%, rgba(106, 17, 190, 0.16) 100%);
}
/*
Main design gradient background for header
*/
.header-gradient {
background: linear-gradient(96.53deg, rgba(145, 17, 190, 0.67) 0.88%, rgba(117, 17, 190, 0.72) 15.29%, rgba(106, 17, 190, 0.81) 34.16%, rgba(106, 17, 190, 0.75) 80.85%, rgba(106, 17, 190, 0.64) 96.25%);
}

.before-hamburger .elementor-menu-toggle::before {
    content: "MENU";
    color: #FFE4FF;
    padding-right: 16px
}

/* add to id of icon list widget to creat custom design */
#design-lis div ul li {
    border-radius: 10px 10px 10px 0;
    background-color: white;
    padding: 0.25rem 1rem;
    box-shadow: 0px 1px 4px #DD569B;
    margin: 2%;
}

/* bug fix for blog post pagination */
.pagination-fix .elementor-pagination a {
    font-size: 2rem;
}

